func TestMaximumInvitations(t *testing.T) {

	for _, tt := range testcases {
		t.Run(tt.name, func(t *testing.T) {
			if got := MaximumInvitations(tt.args); got != tt.want {
				t.Errorf("MaximumInvitations() = %v, want %v", got, tt.want)
			}
		})
	}
}
